var outTemp_idx = 95;
/*** END Configuration ****/



var weather =msg.payload;
var newMsg;

// console.log(weather);
if ( weather.outTemp_C === undefined || outTemp_idx === null )
    return null;

var baro = parseFloat(weather.barometer_mbar).toFixed(2);
var humidity = parseFloat(weather.outHumidity).toFixed(1);
var temp = parseFloat(weather.outTemp_C).toFixed(1);
var battery = parseInt(weather.outTempBatteryStatus);
var humstat;

switch ( battery ) {
    case 0:
        battery = 100;
        break;
    default:
        battery = 10;
        break;
}

if ( 35 < humidity < 55 )
    humstat = 1;
else if ( 25 <= humidity <= 65 )
    humstat = 0;
else if ( humidity < 25 )
    humstat = 2;
else if (humidity > 65 )
    humstat = 3;

//console.log('out Temp = ' + weather.outTemp_C);
//console.log('Out Humidity = '+  weather.outHumidity);
//console.log('Barometer = '+  weather.barometer_mbar);
// Format de sValue = Temp;HUm;Hum_Stat;Bar;Prevision (see https://www.domoticz.com/wiki/MQTT)
newMsg = {'command': 'udevice','idx' : outTemp_idx,'battery': battery,'nvalue' : 0, 'svalue':temp+';'+humidity+';'+humstat+';'+baro+';0' };
var outTemp = {"payload" : newMsg };
return outTemp;

/*** Set your Domoticz Sensor IDX here ***/
var Wind_idx = 96 ;
/*** END Configuration ****/


var weather = msg.payload;
var newMsg;

// console.log(weather);
if ( weather.windSpeed_kph === undefined || Wind_idx === null )
    return null;

var windRose;
var windDir;

if ( weather.windDir  === undefined ) {
    windDir = '-';
    windRose = '-';
} else {
    windDir = parseFloat(weather.windDir).toFixed(2);

    if ( windDir < 11.25 || windDir >= 348.25 )
        windRose = 'N';
    else if ( windDir >= 11.25 && windDir < 33.75 )
        windRose = 'NNE';
    else if ( windDir >= 33.75 && windDir < 56.25 )
        windRose = 'NE';
    else if ( windDir >= 56.25 && windDir < 78.75 )
        windRose = 'ENE';
    else if ( windDir >= 78.75 && windDir < 101.25 )
        windRose = 'E';
    else if ( windDir >= 101.25 && windDir < 123.75 )
        windRose = 'ESE';
    else if ( windDir >= 123.75 && windDir < 146.25 )
        windRose = 'SE';
    else if ( windDir >= 146.25 && windDir < 168.75 )
        windRose = 'SSE';
    else if ( windDir >= 168.75 && windDir < 191.25 )
        windRose = 'S';
    else if ( windDir >= 191.25 && windDir < 213.75 )
        windRose = 'SSO';
    else if ( windDir >= 213.75 && windDir < 236.25 )
        windRose = 'SO';
    else if ( windDir >= 236.25 && windDir < 258.75 )
        windRose = 'OSO';
    else if ( windDir >= 258.75 && windDir < 281.25 )
        windRose = 'O';
    else if ( windDir >= 281.25 && windDir < 303.75 )
        windRose = 'ONO';
    else if ( windDir >= 303.75 && windDir < 326.25 )
        windRose = 'NO';
    else if ( windDir >= 326.25 && windDir < 348.75 )
        windRose = 'NNO';
}

windSpeed = parseFloat(weather.windSpeed_kph)*100/36; // 10*WS (m/s)
windGust = parseFloat(weather.windGust_kph)*100/36; //10*WG (m/s)
outTemp = parseFloat(weather.outTemp_C).toFixed(2);
windchill = parseFloat(weather.windchill_C).toFixed(2);
// Format de sValue = WindDir;WindRose;Speed;Gust;Temp;Chill (see https://www.domoticz.com/wiki/Domoticz_API/JSON_URL%27s#Wind)
newMsg = {'command': 'udevice','idx' : Wind_idx ,'nvalue' : 0, 'svalue': windDir +';'+windRose+';'+windSpeed.toFixed(2)+';'+windGust.toFixed(2)+';'+outTemp+';'+windchill} ;
var windMsg = {"payload" : newMsg };
return windMsg;

